<?php/* Add this on all pages on top. */
set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';
require_once 'config/smarty.php';/** * Check for login */require_once 'administration/includes/auth.php';require_once 'class/event.php';$eventObject = new class_event();  if(isset($_GET['delete_code'])) {		$errorArray				= array();	$errorArray['error']	= '';	$errorArray['result']	= 0;		$formValid				= true;	$success					= NULL;	$code						= trim($_GET['delete_code']);			if($errorArray['error']  == '' && $errorArray['result']  == 0 ) {		$data	= array();		$data['event_deleted'] = 1;				$where = $eventObject->getAdapter()->quoteInto('event_code = ?', $code);		$success	= $eventObject->update($data, $where);					if(is_numeric($success) && $success > 0) {			$errorArray['error']	= '';			$errorArray['result']	= 1;					} else {			$errorArray['error']	= 'Could not delete, please try again.';			$errorArray['result']	= 0;						}	}		echo json_encode($errorArray);	exit;}/* Setup Pagination. */$eventData = $eventObject->getAll('event_deleted = 0','event.event_added');if($eventData) $smarty->assign_by_ref('eventData', $eventData);/* End Pagination Setup. */$smarty->display('administration/events/default.tpl');?>