<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';/* objects. */require_once 'class/gallery.php';$galleryObject 			= new class_gallery();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$galleryData = $galleryObject->getByCode($code);		if($galleryData) {		$smarty->assign('galleryData', $galleryData);	} else {		header('Location: /administration/gallery/');		exit;			}}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['gallery_name']) && trim($_POST['gallery_name']) == '') {		$errorArray['gallery_name'] = 'required';		$formValid = false;			}		if(isset($_POST['gallery_description']) && trim($_POST['gallery_description']) == '') {		$errorArray['gallery_description'] = 'required';		$formValid = false;			}	if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();						$data['gallery_name']		= trim($_POST['gallery_name']);				$data['gallery_description']	= trim($_POST['gallery_description']);							if(isset($galleryData)) {			/*Update. */			$where		= $galleryObject->getAdapter()->quoteInto('gallery_code = ?', $galleryData['gallery_code']);			$success	= $galleryObject->update($data, $where);									$success 	= $galleryData['gallery_code'];		} else {			$success = $galleryObject->insert($data);		}				if(count($errorArray) == 0) {			header('Location: /administration/gallery/image.php?code='.$success);				exit;				}	}	/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('administration/gallery/details.tpl');?>