<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/*** Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';/*** Check for login */require_once 'administration/includes/auth.php';/* objects. */require_once 'class/participantcategory.php';$participantcategoryObject 	= new class_participantcategory();if (isset($_GET['code']) && trim($_GET['code']) != '') {		$code = trim($_GET['code']);		$participantcategoryData = $participantcategoryObject->getByCode($code);	if(!$participantcategoryData) {		header('Location: /administration/participants/categories/');		exit;	}	$smarty->assign('participantcategoryData', $participantcategoryData);}/* Check posted data. */if(count($_POST) > 0) {	$errorArray		= array();	$data 				= array();	$formValid		= true;	$success			= NULL;		if(isset($_POST['participantcategory_name']) && trim($_POST['participantcategory_name']) == '') {		$errorArray['participantcategory_name'] = 'required';		$formValid = false;			}		if(count($errorArray) == 0 && $formValid == true) {				$data 	= array();						$data['participantcategory_name']		= trim($_POST['participantcategory_name']);									if(isset($participantcategoryData)) {					/*Update. */			$where		= $participantcategoryObject->getAdapter()->quoteInto('participantcategory_code = ?', $participantcategoryData['participantcategory_code']);			$success	= $participantcategoryObject->update($data, $where);											} else {								$success = $participantcategoryObject->insert($data);					}				if(count($errorArray) == 0) {										header('Location: /administration/participants/categories/');				exit;				}				}		/* if we are here there are errors. */	$smarty->assign('errorArray', $errorArray);	}$smarty->display('administration/participants/categories/details.tpl');?>