<?php/* Add this on all pages on top. */set_include_path($_SERVER['DOCUMENT_ROOT'].'/'.PATH_SEPARATOR.$_SERVER['DOCUMENT_ROOT'].'/library/classes/');/** * Standard includes */require_once 'config/database.php';require_once 'config/smarty.php';require_once 'class/area.php';$areaObject	= new class_area();$results 				= array();$list						= array();	if(isset($_REQUEST['term'])) {			$q			= strtolower(trim($_REQUEST['term'])); 	$areaData	= $areaObject->search($q);		if($areaData) {		for($i = 0; $i < count($areaData); $i++) {			$list[] = array(				"id" 		=> $areaData[$i]["area_code"],				"label" 	=> $areaData[$i]['area_name'].', '.$areaData[$i]['parent_name'],				"value" 	=> $areaData[$i]['area_name'].', '.$areaData[$i]['parent_name'],			);					}		}}if(count($list) > 0) {	echo json_encode($list); 	exit;} else {	echo json_encode(array('id' => '', 'label' => 'no results')); 	exit;}exit;?>